/**
 * 영어 알파벳을 대문자로 교체한다.
 */
scwin.toUpperFormatter = function (value) {
	return value.toUpperCase();
};

/**
 * setDisplayFormat의 버튼 클릭시, 포멧 세팅
 */
scwin.btnClick_format = function () {
	var frontId = this.getID().split("_")[1];
	var splitId = this.getID().split("_");
	var splitLen = splitId.length;
	var frontId = splitId[splitLen - 2];
	var backId = splitId[splitLen - 1];
	var fullId = splitId[splitLen - 2] + "_" + splitId[splitLen - 1];
	var btnId = "btn_" + fullId;
	var ibxId = "ibx_" + frontId;
	var componentBtnId = com.util.getComponent(btnId);
	var componentIbxId = com.util.getComponent(ibxId);

	//split[2]의 문자열이 Gv일 때
	if (backId == "Gv") {
		com.win.alert(componentIbxId.getValue()); //ibx의 Value를 알림창에 띄운다.
	}
	//split[2]의 문자열이 Sdf일 때
	else if (backId == "Sdf") {
		//split[1]의 문자열이 PhoneFormat일 때
		if (frontId == "phoneFormat") {
			componentIbxId.setDisplayFormat("###-####-####"); //ibx컴포넌트의 DisplayFormat 속성에 해당 포멧을 적용한다
		}
		//split[1]의 문자열이 MoneyFormat일 때
		else if (frontId == "moneyFormat") {
			componentIbxId.setDisplayFormat("#,###.##0"); //ibx컴포넌트의 DisplayFormat 속성에 해당 포멧을 적용한다
		}
		//split[1]의 문자열이 DateFormat일 때
		else if (frontId == "dateFormat") {
			componentIbxId.setDisplayFormat("yyyy/MM/dd"); //ibx컴포넌트의 DisplayFormat 속성에 해당 포멧을 적용한다
		}
		//split[1]의 문자열이 그 외일 때 (오류)
		else {
			com.win.alert("Sdf Error!");
		}
	}
	//split[2]의 문자열이 Gdf일 때
	else if (backId == "Gdf") {
		com.win.alert(componentIbxId.getDisplayFormat()); //ibx컴포넌트의 DisplayFormat 속성 값을 알림창에 띄운다
	}
	//split[2]의 문자열이 Gdv일 때
	else if (backId == "Gdv") {
		com.win.alert(componentIbxId.getFormattedValue()); //ibx컴포넌트의 DisplayFormat 속성의 Vlaue 값을 알림창에 띄운다
	}
	//split[2]의 문자열이 그 외일 때 (오류)
	else {
		com.win.alert("Format Error!");
	}
};

/**
 * setDisplayFormatter의 버튼 클릭시, 포메터 세팅
 */
scwin.btnClick_formatter = function () {
	var splitId = this.getID().split("_");
	var splitLen = splitId.length;
	var frontId = splitId[splitLen - 2];
	var backId = splitId[splitLen - 1];
	var fullId = splitId[splitLen - 2] + "_" + splitId[splitLen - 1];
	var btnId = "btn_" + fullId;
	var ibxId = "ibx_" + frontId;
	var componentBtnId = $p.getComponentById(btnId);
	var componentIbxId = $p.getComponentById(ibxId);

	//split[2]의 문자열이 Gv일 때
	if (backId == "Gv") {
		//split[1]의 문자열이 ToUpperFormatter일 때
		if (frontId.indexOf("Formatter") > 0) {
			alert(componentIbxId.getValue()); //버튼그룹에 속한 ibx의 ID Value를 알림창에 띄운다.
		} else {
			//ibx의 ID를 얻고 길이를 가공한 후, 컴포넌트로 만들고 Value를 알림창에 띄운다.
			alert(com.util.getComponent(getComponentById.slice(0, componentIbxId.getID().length - 3)).getValue());
		}

	}
	//split[2]의 문자열이 Sdf일 때
	else if (backId == "Sdf") {
		//split[1]의 문자열이 PhoneFormatter일 때
		if (frontId == "phoneFormatter" && scwin.disposableFormat_Phone == false) {
			componentIbxId.setDisplayFormatter("com.str.formatPhone");
			//componentIbxId.setValue(com.str.formatPhone(componentIbxId.getValue())); //ibx의 value에 공통함수(com.str.formatPhone)로 포메터 적용
			scwin.disposableFormat_Phone = true;
		}
		//split[1]의 문자열이 MoneyFormatter일 때
		else if (frontId == "moneyFormatter" && scwin.disposableFormat_Money == false) {
			componentIbxId.setDisplayFormatter("com.num.formatNumber");

			//componentIbxId.setValue(com.num.formatNumber(componentIbxId.getValue(), "won")); //ibx의 value에 공통함수(com.num.formatNumber)로 포메터 적용
			scwin.disposableFormat_Money = true;
		}
		//split[1]의 문자열이 DateFormatter일 때
		else if (frontId == "dateFormatter" && scwin.disposableFormat_Date == false) {
			componentIbxId.setDisplayFormatter("com.date.formatDate");
			//componentIbxId.setValue(com.date.formatDate(componentIbxId.getValue(), "slash")); //ibx의 value에 공통함수(com.date.formatDate)로 포메터 적용
			scwin.disposableFormat_Date = true;
		}
		//이미 포멧터가 적용 됐을 경우
		else {
			com.win.alert("이미 포멧터가 적용 되었습니다.");
		}
	}
	//split[2]의 문자열이 Gdf일 때
	else if (backId == "Gdf") {
		//split[1]의 문자열이 PhoneFormatter일 때
		if (frontId == "phoneFormatter") {
			com.win.alert("com.str.formatPhone"); //사용한 공통함수명을 알림창에 띄운다.
		}
		//split[1]의 문자열이 MoneyFormatter일 때
		else if (frontId == "moneyFormatter") {
			com.win.alert("com.num.formatNumber"); //사용한 공통함수명을 알림창에 띄운다.
		}
		//split[1]의 문자열이 DateFormatter일 때
		else if (frontId == "dateFormatter") {
			com.win.alert("com.date.formatDate"); //사용한 공통함수명을 알림창에 띄운다.
		}
		//split[1]의 문자열이 그 외일 때 (오류)
		else {
			com.win.alert("Gdf Error!");
		}
	}
	//split[2]의 문자열이 Gdv일 때
	else if (backId == "Gdv") {
		com.win.alert(componentIbxId.getFormattedValue()); //포메터가 적용된 Value를 알림창에 띄운다.	
	}
	//split[2]의 문자열이 그 외일 때 (오류)
	else {
		com.win.alert("Formatter Error!");
	}
};

/**
 * 유럽연합 화폐
 * Euro Formatter
 */
scwin.btn_euro_onclick = function () {
	ibx_cmoneyFormatter.changeCurrency("euro");
};

scwin.btn_euroFormatter_onclick = function () {
	ibx_cmoneyFormatter.changeCurrency("euro", "#`###~00", "`", "~");
};

/**
 * 인도 화폐
 * Rupee Formatter
 */
scwin.btn_rupee_onclick = function () {
	ibx_cmoneyFormatter.changeCurrency("rupee");
};

scwin.btn_rupeeFormatter_onclick = function () {
	ibx_cmoneyFormatter.changeCurrency("rupee", "##|##|###=00", "|", "=");
};

/**
 * 카자흐스탄 화폐
 * Tenge Formatter
 */
scwin.btn_tenge_onclick = function () {
	ibx_cmoneyFormatter.changeCurrency("tenge");
};

scwin.btn_tengeFormatter_onclick = function () {
	ibx_cmoneyFormatter.changeCurrency("tenge", "#@###:00", "@", ":");
};

/**
 * 숫자
 * Number Formatter
 */
scwin.btn_number_onclick = function () {
	ibx_cmoneyFormatter.changeCurrency("number");
};

scwin.btn_numberFormatter_onclick = function () {
	ibx_cmoneyFormatter.changeCurrency("number", "#_###/00", "_", "/");
};
